import React, { useState } from 'react'
import ChangePassword from '../pages/ChangePassword'
import RefreshToken from '../pages/RefreshToken'

const UserMenu = () => {
  const [open, setOpen] = useState(false)
  const [activeTab, setActiveTab] = useState(null)

  const handleLogout = () => {
    console.log('Log: Kullanıcı çıkış yaptı (LO)')
    localStorage.clear()
    window.location.href = '/'
  }

  return (
    <div className="flex justify-end mb-4 relative">

      <button
        onClick={() => setOpen(!open)}
        className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300"
      >
        {localStorage.getItem('username') || 'Kullanıcı'}
      </button>

      {open && (
        <div
        className={`absolute top-full right-0 mt-2 bg-white shadow rounded w-48 z-10 transition-all duration-300 ease-out ${
          open ? 'opacity-100 scale-100' : 'opacity-0 scale-95 pointer-events-none'
        }`}
      >

          <button onClick={() => setActiveTab('password')} className="block w-full text-left px-4 py-2 hover:bg-gray-100">Parola Değiştir</button>
          <button onClick={() => setActiveTab('token')} className="block w-full text-left px-4 py-2 hover:bg-gray-100">Token Yenile</button>
          <button onClick={handleLogout} className="block w-full text-left px-4 py-2 hover:bg-gray-100 text-red-600">Çıkış Yap</button>
        </div>
      )}

      {/* Menüden seçilen içerik */}
      <div className="absolute right-0 top-16 w-full max-w-md">
        {activeTab === 'password' && <ChangePassword />}
        {activeTab === 'token' && <RefreshToken />}
      </div>
    </div>
  )
}

export default UserMenu
