export const validateLogin = ({ username, password }) => {
  const errors = {}
  if (!username) errors.username = 'Kullanıcı adı gerekli'
  if (!password) errors.password = 'Parola gerekli'
  return { hasErrors: Object.keys(errors).length > 0, errors }
}
