import React, { useState } from 'react'
import ChangePassword from '../pages/ChangePassword'
import RefreshToken from '../pages/RefreshToken'

const UserMenu = () => {
  const [open, setOpen] = useState(false)
  const [activeTab, setActiveTab] = useState(null)

  const handleLogout = () => {
    console.log('Log: Kullanıcı çıkış yaptı (LO)')
    localStorage.clear()
    window.location.href = '/'
  }

  return (
    <div className="flex justify-end mb-4 relative">
      {/* Kullanıcı butonu */}
      <button
        onClick={() => setOpen(!open)}
        className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300"
      >
        {localStorage.getItem('username') || 'Kullanıcı'}
      </button>

      {/* Açılır menü */}
      {open && (
        <div
          className={`absolute top-full right-0 mt-2 bg-white shadow rounded w-48 z-10 transition-all duration-300 ease-out ${
            open ? 'opacity-100 scale-100' : 'opacity-0 scale-95 pointer-events-none'
          }`}
        >
          <button
            onClick={() => setActiveTab('password')}
            className="block w-full text-left px-4 py-2 hover:bg-blue-50"
          >
            Parola Değiştir
          </button>
          <button
            onClick={() => setActiveTab('token')}
            className="block w-full text-left px-4 py-2 hover:bg-blue-50"
          >
            Token Yenile
          </button>
          <button
            onClick={handleLogout}
            className="block w-full text-left px-4 py-2 hover:bg-blue-50 text-red-600"
          >
            Çıkış Yap
          </button>
        </div>
      )}

      {/* Modal içerikler */}
      {activeTab === 'password' && <ChangePassword onClose={() => setActiveTab(null)} />}
      {activeTab === 'token' && <RefreshToken onClose={() => setActiveTab(null)} />}
    </div>
  )
}

export default UserMenu
