// src/pages/Users.jsx
import React, { useEffect, useState } from 'react'
import { getUsers } from '../services/api'

const Users = () => {
  const [users, setUsers] = useState([])
  const role = localStorage.getItem('role')
  const currentUser = localStorage.getItem('username')

  useEffect(() => {
    getUsers().then(res => setUsers(res.data))
  }, [])

  const filteredUsers = role === 'Admin'
    ? users
    : users.filter(u => u.username === currentUser)

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Kullanıcılar</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">Ad Soyad</th>
            <th className="p-2">E-posta</th>
            <th className="p-2">Telefon</th>
            <th className="p-2">Rol</th>
            {role === 'Admin' && <th className="p-2">İşlemler</th>}
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map(u => (
            <tr key={u.id} className={`border-t ${u.status === 'passive' ? 'bg-red-100' : ''}`}>
              <td className="p-2">{u.firstName} {u.lastName}</td>
              <td className="p-2">{u.email}</td>
              <td className="p-2">{u.phoneNumber}</td>
              <td className="p-2">{u.role}</td>
              {role === 'Admin' && (
                <td className="p-2 space-x-2">
                  <button className="text-blue-600">Düzenle</button>
                  <button className="text-red-600">Pasife Al</button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default Users
